import React, { useEffect } from 'react';

const About = () => {
    useEffect(() => {
        // Dynamic Background Logic Removed: Background is now handled globally by CSS animation.
    }, []);

    return (
        <div className="pages">
            <h2 style={{ color: 'var(--primary)', marginBottom: '15px' }}>About the Workout Recorder</h2>
            <p>
                The Workout Recorder is designed as a simple, powerful tool for fitness enthusiasts to track their daily activities and progress.
                This application serves as the user interface for the Health Track - Fitness and Wellness system.
            </p>
            <p>
                <strong>Our Mission:</strong> To provide a distraction-free environment for logging essential workout metrics—title, load (in kg), and repetitions (reps)—allowing users to focus entirely on their training and see their history at a glance.
            </p>
            <p>
                This frontend is built using ReactJS, utilizing the Context API for state management, and is fully responsive, ensuring a seamless experience across all devices, from mobile phones to desktops.
            </p>
        </div>
    );
}

export default About;
