import { useWorkoutsContext } from '../hooks/useWorkoutContext'
// We need to install date-fns in a real project: npm install date-fns
// Assuming date-fns is available via package.json
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

const WorkoutDetails = ({ workout }) => {
    // Get dispatch function from context to update the global state
    const { dispatch } = useWorkoutsContext()

    const handleClick = async () => {
        // Simulate a DELETE request to the backend API
        const response = await fetch('/api/workouts/' + workout._id, {
            method: 'DELETE'
        })
        const json = await response.json()

        if (response.ok) {
            // Dispatch action to remove the workout from global state
            dispatch({type: 'DELETE_WORKOUT', payload: json})
        } else {
            console.error('Failed to delete workout:', json.error);
        }
    }

    return ( 
        <div className="workout-details">
            {/* Workout data display */}
            <h4>{workout.title}</h4>
            <p><strong>Load(kg): </strong>{workout.load}</p>
            <p><strong>Reps: </strong>{workout.reps}</p>
            {/* Display time since creation (assuming workout.createdAt is provided) */}
            <p>
                {/* Fallback to 'just now' if createdAt is missing or invalid for the demo */}
                {workout.createdAt ? formatDistanceToNow(new Date(workout.createdAt), { addSuffix: true }) : 'just now'}
            </p>
            {/* Delete button (styled as a span) */}
            <span onClick={handleClick} className="material-symbols-outlined delete-icon">
                delete
            </span>
        </div>
     );
}

export default WorkoutDetails;
