import React, { useEffect } from 'react';

const Features = () => {
    useEffect(() => {
        // Dynamic Background Logic Removed: Background is now handled globally by CSS animation.
    }, []);

    return (
        <div className="pages">
            <h2 style={{ color: 'var(--primary)', marginBottom: '15px' }}>Key Features</h2>
            <ul style={{ listStyle: 'disc', marginLeft: '20px', lineHeight: '1.8' }}>
                <li><strong>Real-Time Logging:</strong> Instantly add new workout sessions with title, load, and repetition details.</li>
                <li><strong>State Management:</strong> Uses React Context API and a reducer pattern for efficient, predictable state updates.</li>
                <li><strong>Instant Deletion:</strong> Quickly remove workouts from the dashboard using the mock API delete functionality.</li>
                <li><strong>Responsive UI:</strong> The layout dynamically adjusts for optimal viewing on any device size.</li>
                <li><strong>Modular Components:</strong> Clean, reusable components for the form, workout details, and navigation.</li>
                <li><strong>Mock Data Integration:</strong> Functional frontend demonstrates core logic without requiring a live backend server connection (for Phase 1 completion).</li>
            </ul>
        </div>
    );
}

export default Features;
