const Features = () => {
    return ( 
        <div className="static-page">
            <h2>Core Features</h2>
            <p>The Workout Tracker is designed to streamline your fitness logging process with powerful tools:</p>

            <ul className="feature-list">
                <li><strong>Dynamic Workout Log:</strong> Easily record the title, load (kg), and repetitions for any exercise.</li>
                <li><strong>Real-time Deletion:</strong> Quickly remove logged workouts using the integrated delete function.</li>
                <li><strong>Time Tracking:</strong> See exactly how long ago each workout was logged, providing a sense of consistency.</li>
                <li><strong>Responsive Design:</strong> The interface adapts seamlessly to desktop, tablet, and mobile devices.</li>
                <li><strong>State Management:</strong> Uses React Context and Reducers for predictable and scalable state handling.</li>
            </ul>

            <p>Future iterations will include goal setting, meal tracking, and detailed progress charts.</p>
        </div>
     );
}
 
export default Features;
