import { useState } from "react";
import { useWorkoutsContext } from "../hooks/useWorkoutContext";

const WorkoutForm = () => {
    const { dispatch } = useWorkoutsContext()
    const [title, setTitle] = useState('')
    const [reps, setReps] = useState('')
    const [load, setLoad] = useState('')
    const [error, setError] = useState(null)
    const [emptyFields, setEmptyFields] = useState([])

    const handleSubmit = async (e) => {

        e.preventDefault()

        const workout = {title, load, reps}


        const currentEmptyFields = [];
        if (!title) currentEmptyFields.push('title');
        if (!load) currentEmptyFields.push('load');
        if (!reps) currentEmptyFields.push('reps');

        if (currentEmptyFields.length > 0) {
            setError('Please fill in all fields.');
            setEmptyFields(currentEmptyFields);
            return; // Stop form submission
        }



        /* const response = await fetch('/api/workouts', {
            method: 'POST',
            body: JSON.stringify(workout),
            headers:{
                "Content-Type": 'application/json'
            }
        })
        const json = await response.json()

        if (!response.ok) {
            setError(json.error)
            setEmptyFields(json.emptyFields || [])
            return
        }
        */

        // --- START: MOCK API SUCCESS RESPONSE ---
        // Simulate a successful response from the backend 
        const json = {
            _id: Date.now().toString(), // Use timestamp as a unique ID
            createdAt: new Date().toISOString(),
            ...workout
        };
        // --- END: MOCK API SUCCESS RESPONSE ---


        // This logic runs now because we are successfully mocking the response (json is defined)
        setTitle('')
        setReps('')
        setLoad('')
        setError(null)
        setEmptyFields([])
        console.log("New Workout added (MOCKED)", json)
        dispatch({type: 'CREATE_WORKOUT', payload: json})
    }

    return ( 
        <form  className="create" onSubmit={handleSubmit}> 
            <h3>Add a New Workout:</h3>

            <label>Exercise Title:</label>
            {/* The 'error' class is applied if 'title' is in the emptyFields array */}
            <input type="text" onChange ={(e) => setTitle(e.target.value)} value={title} className={emptyFields.includes('title') ? 'error' : ''} />

            <label>Load(kg):</label>
            <input type="number" onChange ={(e) => setLoad(e.target.value)} value={load} className={emptyFields.includes('load') ? 'error' : ''}/>

            <label>Reps:</label>
            <input type="number" onChange ={(e) => setReps(e.target.value)} value={reps} className={emptyFields.includes('reps') ? 'error' : ''}/>

            <button>Add Workout</button>
            {error && <div className="error">{error}</div>}
        </form>
     );
}

export default WorkoutForm;
