import { useState } from "react";
import { useWorkoutsContext } from "../hooks/useWorkoutContext";

const WorkoutForm = () => {
    // Get dispatch function from context to update the global state
    const { dispatch } = useWorkoutsContext()

    // Local state for form inputs
    const [title, setTitle] = useState('')
    const [reps, setReps] = useState('')
    const [load, setLoad] = useState('')

    // State for handling form errors and empty fields
    const [error, setError] = useState(null)
    const [emptyFields, setEmptyFields] = useState([])

    const handleSubmit = async (e) => {
        // Prevent default form submission behavior (page refresh)
        e.preventDefault()

        const workout = {title, load, reps}

        // Simulate a POST request to the backend API
        const response = await fetch('/api/workouts', {
            method: 'POST',
            body: JSON.stringify(workout),
            headers:{
                "Content-Type": 'application/json'
            }
        })
        const json = await response.json()

        if (!response.ok) {
            // Set error and list of missing fields if the backend validation fails
            setError(json.error)
            setEmptyFields(json.emptyFields)
        }
        if (response.ok) {
            // Reset form fields and error states on success
            setTitle('')
            setReps('')
            setLoad('')
            setError(null)
            setEmptyFields([])
            console.log("New Workout added", json)
            // Dispatch action to update global state with the new workout
            dispatch({type: 'CREATE_WORKOUT', payload: json})
        }
    }

    return ( 
        // Form structure with controlled inputs and conditional styling for errors
        <form  className="create" onSubmit={handleSubmit}> 
            <h3>Add a New Workout:</h3>

            <label>Exercise Title:</label>
            <input 
                type="text" 
                onChange ={(e) => setTitle(e.target.value)} 
                value={title} 
                // Apply 'error' class if 'title' is in the emptyFields array
                className={emptyFields.includes('title') ? 'error' : ''} 
            />

            <label>Load(kg):</label>
            <input 
                type="number" 
                onChange ={(e) => setLoad(e.target.value)} 
                value={load} 
                className={emptyFields.includes('load') ? 'error' : ''}
            />

            <label>Reps:</label>
            <input 
                type="number" 
                onChange ={(e) => setReps(e.target.value)} 
                value={reps} 
                className={emptyFields.includes('reps') ? 'error' : ''}
            />

            <button>Add Workout</button>
            {/* Display error message if it exists */}
            {error && <div className="error">{error}</div>}
        </form>
     );
}

export default WorkoutForm;
