import { useEffect } from "react";
import WorkoutForm from "../components/WorkoutForm";
import { useWorkoutsContext } from "../hooks/useWorkoutContext"

// Components
import WorkoutDetails from "../components/WorkoutDetails";

const Home = () => {
    // Destructure state (workouts) and dispatch function from context
    const {workouts, dispatch} = useWorkoutsContext()


    useEffect(() => {
        const fetchWorkouts = async () => {
            // NOTE: In a real app, this URL '/api/workouts/' would hit your backend
            // For Phase 1, we will allow this to fail if no backend is running, 
            // but the structure is correct.
            const response = await fetch('/api/workouts/')
            const json = await response.json()

            if (response.ok) {
                // Update the global state with fetched workouts
                dispatch({type:'SET_WORKOUTS', payload: json})
            } else {
                // Optional: handle error here if fetch fails (e.g., set error state)
                console.error('Failed to fetch workouts:', json.error);
            }
        }
        fetchWorkouts()
    },[dispatch])

    return ( 
        <div className="home">
            <div className="workouts">
                {/* Check if workouts exist before mapping */}
                {workouts && workouts.map((workout) => (
                    // Display each workout using the WorkoutDetails component
                    <WorkoutDetails key={workout._id} workout={workout} />
                ))}
                {workouts === null && <p>Loading workouts...</p>}
                {workouts && workouts.length === 0 && <p>No workouts logged yet. Add one!</p>}
            </div>
            {/* Form to add new workouts */}
            <WorkoutForm />
        </div>
     );
}

export default Home;
