import { useEffect } from "react";
import WorkoutForm from "../components/WorkoutForm";
import { useWorkoutsContext } from "../hooks/useWorkoutContext"

// Components
import WorkoutDetails from "../components/WorkoutDetails";

// Mock data for initial loading when no backend is available
const mockInitialWorkouts = [
    { _id: 'mock1', title: 'Bench Press', load: 80, reps: 5, createdAt: new Date(Date.now() - 3600000).toISOString() },
    { _id: 'mock2', title: 'Squat', load: 100, reps: 3, createdAt: new Date(Date.now() - 7200000).toISOString() },
    { _id: 'mock3', title: 'Deadlift', load: 120, reps: 1, createdAt: new Date(Date.now() - 10800000).toISOString() }
];

const Home = () => {
    const {workouts, dispatch} = useWorkoutsContext()


    useEffect(() => {
        const fetchWorkouts = async () => {
            // NOTE: The original fetch call is commented out below.
            // If you have a backend server running on port 4000, uncomment the lines below.

            /*
            const response = await fetch('/api/workouts/')
            const json = await response.json()

            if (response.ok) {
                dispatch({type:'SET_WORKOUTS', payload: json})
            }
            */

            // --- START: MOCK API INITIAL LOAD ---
            console.log("Loading initial workouts (MOCKED)")
            // Simulate a network delay
            await new Promise(resolve => setTimeout(resolve, 500)); 

            // Dispatch mock data
            dispatch({type:'SET_WORKOUTS', payload: mockInitialWorkouts})
            // --- END: MOCK API INITIAL LOAD ---

        }
        fetchWorkouts()
    },[dispatch])

    return ( 
        <div className="home">
            <div className="workouts">
                {workouts && workouts.map((workout) => (
                    <WorkoutDetails key={workout._id} workout={workout} />
                ))}
            </div>
            <WorkoutForm />
        </div>
     );
}

export default Home;
