import { useWorkoutsContext } from '../hooks/useWorkoutContext'
// Ensure you have installed date-fns: npm install date-fns
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

const WorkoutDetails = ({ workout }) => {
    const { dispatch } = useWorkoutsContext()

    const handleClick = async () => {

        // NOTE: The original fetch call is commented out below.
        // If you have a backend server running on port 4000, uncomment the lines below.

        /*
        const response = await fetch('/api/workouts/' + workout._id, {
            method: 'DELETE'
        })
        const json = await response.json()

        if (response.ok) {
            dispatch({type: 'DELETE_WORKOUT', payload: json})
        }
        */

        // --- START: MOCK API DELETE RESPONSE ---
        // Simulate a successful deletion response by dispatching the workout object itself
        console.log(`Deleting workout ${workout._id} (MOCKED)`)
        dispatch({type: 'DELETE_WORKOUT', payload: workout})
        // --- END: MOCK API DELETE RESPONSE ---
    }

    return ( 
        <div className="workout-details">
            <h4>{workout.title}</h4>
            <p><strong>Load (kg): </strong>{workout.load}</p>
            <p><strong>Reps: </strong>{workout.reps}</p>
            {/* Format the timestamp to be readable (e.g., '3 hours ago') */}
            <p>{formatDistanceToNow(new Date(workout.createdAt), { addSuffix: true })}</p>
            <span onClick={handleClick} title="Delete Workout"></span>
        </div>
     );
}

export default WorkoutDetails;
