import { useEffect } from "react";
import WorkoutForm from "../components/WorkoutForm";
import { useWorkoutsContext } from "../hooks/useWorkoutContext"

// Components
import WorkoutDetails from "../components/WorkoutDetails";

const Home = () => {
    const {workouts, dispatch} = useWorkoutsContext()


    useEffect(() => {
        const fetchWorkouts = async () => {
            // NOTE: The original fetch call to the backend is commented out.

            /*
            const response = await fetch('/api/workouts/')
            const json = await response.json()

            if (response.ok) {
                dispatch({type:'SET_WORKOUTS', payload: json})
            }
            */

            // --- START: MOCK API INITIAL LOAD (Now sets an empty list) ---
            console.log("Initializing workouts list (MOCKED) - starting empty.")
            // Simulate a network delay
            await new Promise(resolve => setTimeout(resolve, 500)); 

            // Dispatch an empty array to ensure the list starts blank
            dispatch({type:'SET_WORKOUTS', payload: []})
            // --- END: MOCK API INITIAL LOAD ---

        }
        fetchWorkouts()
    },[dispatch])

    return ( 
        <div className="home">
            <div className="workouts">
                {workouts && workouts.length > 0 ? (
                    // Display workouts if the array is not empty
                    workouts.map((workout) => (
                        <WorkoutDetails key={workout._id} workout={workout} />
                    ))
                ) : (
                    // Display message if the workouts array is initialized but empty
                    <p className="p-4 bg-white rounded-lg shadow-md text-gray-500 text-center italic">
                        No workouts yet. Add one using the form on the right!
                    </p>
                )}
            </div>
            <WorkoutForm />
        </div>
     );
}

export default Home;
